import React from "react";
import Images from '../images/4.png'; // Ensure correct image path

const Promotion = () => {
  return (
    <div className="relative h-[75vh] flex items-center justify-center text-white">
      {/* Background Image */}
      <img src={Images} alt="Promotion" className="absolute inset-0 w-full h-full object-cover" />

      {/* Blue Overlay for Readability */}
      <div className="absolute inset-0 bg-blue-900 bg-opacity-50"></div>

      {/* Content Box with Blue Tint */}
      <div className="relative z-10 text-center p-8 max-w-4xl bg-blue-800 bg-opacity-60 rounded-lg shadow-lg backdrop-blur-md">
        <h1 className="text-5xl font-extrabold mb-4 drop-shadow-lg tracking-wide">
          Exclusive Deals to Southeast Asia
        </h1>
        <p className="text-lg font-medium drop-shadow-lg">
          We have special fares with <span className="font-semibold">Korean Air</span>, <span className="font-semibold">China Airlines</span>, 
          <span className="font-semibold"> EVA Air</span>, and <span className="font-semibold">Cathay Pacific</span>.
        </p>
      </div>
    </div>
  );
};

export default Promotion;
