import React from "react";
import Images from '../images/4.png'; // Ensure correct image path

const Promotion = () => {
  return (
    <div className="relative h-[75vh] flex items-center justify-center text-white">
      {/* Background Image */}
      <img src={Images} alt="Promotion" className="absolute inset-0 w-full h-full object-cover" />
      
      {/* Darker Overlay for Better Contrast */}
      <div className="absolute inset-0 bg-black bg-opacity-40"></div>

      {/* Content Box with Blur Effect for Better Readability */}
      <div className="relative z-10 text-center p-8 max-w-3xl backdrop-blur-md bg-black/30 rounded-lg shadow-lg">
        <h1 className="text-5xl font-extrabold mb-4 drop-shadow-lg">
          Exclusive Deals to Southeast Asia
        </h1>
        <p className="text-lg font-medium drop-shadow-lg leading-relaxed">
          We have special fares with 
          <span className="font-semibold"> Korean Air</span>,  
          <span className="font-semibold"> China Airlines</span>,  
          <span className="font-semibold"> EVA Air</span>, and  
          <span className="font-semibold"> Cathay Pacific</span>.
        </p>
      </div>
    </div>
  );
};

export default Promotion;
